mapper.select:
SELECT solr_sort.`solr_sort_id`, solr_sort.`solr_sort_name`, DATE_FORMAT(solr_sort.`solr_sort_ctime`,'%Y-%m-%d %H:%i:%s') AS `solr_sort_ctime`, DATE_FORMAT(solr_sort.`solr_sort_mtime`,'%Y-%m-%d %H:%i:%s') AS `solr_sort_mtime`, solr_sort.`solr_sort_code`, solr_sort.`solr_sort_useboost`, solr_sort.`solr_index_id`, solr_sort.`xt_userinfo_id`, xt_userinfo.`xt_userinfo_realName` FROM solr_sort solr_sort LEFT JOIN xt_userinfo AS xt_userinfo ON xt_userinfo.xt_userinfo_id = solr_sort.xt_userinfo_id WHERE 1=1
AND solr_sort.solr_index_id=#{solr_index_id}
AND solr_sort.solr_sort_name like '%${solr_sort_name}%'
