mapper.select:
SELECT `b_seller_express_id`, `b_seller_id`, `b_seller_express_name`, `b_seller_express_status`, DATE_FORMAT(`b_seller_express_ctime`,'%Y-%m-%d %H:%i:%s') AS `b_seller_express_ctime`, DATE_FORMAT(`b_seller_express_mtime`,'%Y-%m-%d %H:%i:%s') AS `b_seller_express_mtime` FROM b_seller_express WHERE 1=1
AND instr(b_seller_express_name,#{b_seller_express_name})
AND b_seller_express_status = #{b_seller_express_status}
AND b_seller_id = #{b_seller_id}
