mapper.select:
SELECT b_product.`b_product_name`, b_category.`b_category_name`, b_brand.`b_brand_name`, b_seller_product.`b_seller_product_id`, b_seller_product.`b_seller_id`, b_seller_product.`b_product_id`, b_seller_product.`b_seller_product_status`, b_seller.`b_seller_name` FROM b_product b_product LEFT JOIN xt_userinfo xt_userinfo ON b_product.xt_userinfo_id = xt_userinfo.xt_userinfo_id LEFT JOIN b_brand b_brand ON b_product.b_brand_id = b_brand.b_brand_id LEFT JOIN b_category b_category ON b_category.b_category_id = b_product.b_category_id LEFT JOIN b_seller_product b_seller_product ON b_seller_product.b_product_id = b_product.b_product_id LEFT JOIN b_seller b_seller ON b_seller.b_seller_id = b_seller_product.b_seller_id WHERE 1=1
AND instr(b_product.b_product_name,#{b_product_name})
AND b_seller_product.b_seller_product_status = #{b_seller_product_status}
AND b_seller_product.`b_seller_id` = #{b_seller_id}
