mapper.select:
SELECT lc_apply.`lc_apply_id`, lc_apply.`lc_apply_title`, lc_apply.`lc_apply_model_id`, lc_apply.`lc_apply_delete`, lc_apply.`processInstance_id`, lc_apply.`processdefinitions_id`, lc_apply.`lc_url`, lc_apply.`xt_userinfo_id`, DATE_FORMAT(lc_apply.`lc_apply_time`,'%Y-%m-%d %H:%i:%s') AS `lc_apply_time`, lc_apply.`lc_apply_model_biz_id`, xt_constant.`xt_constantRemark`, xt_constant.`xt_constantURL` FROM lc_apply lc_apply LEFT JOIN xt_constant xt_constant ON lc_apply.lc_apply_model_id = xt_constant.xt_constant_id WHERE lc_apply.lc_apply_isonlysaverecord = 0
AND instr(lc_apply.lc_apply_title,#{lc_apply_title})
AND lc_apply.`xt_userinfo_id` = #{xt_userinfo_id }
AND lc_apply.`processInstance_id` = #{processInstance_id }
AND lc_apply.`lc_apply_model_biz_id` = #{lc_apply_model_biz_id }
ORDER BY lc_apply_time DESC
