mapper.select:
SELECT solr_filed_copy.`solr_filed_copy_id`, solr_filed_copy.`solr_filed_copy_remark`, solr_filed_copy.`solr_filed_copy_source_id`, solr_filed_copy.`solr_filed_copy_dest_id`, solr_filed_copy.`solr_filed_copy_ctime`, solr_filed_copy.`solr_filed_copy_mtime`, solr_filed_copy.`xt_userinfo_id`, solr_filed_copy.`solr_core_id`, solr_filed_copy_source.solr_index_name as solr_filed_copy_source_name, solr_filed_copy_source.solr_index_remark as solr_filed_copy_source_index, solr_filed_copy_dest.solr_index_name as solr_filed_copy_dest_name, solr_filed_copy_dest.solr_index_remark as solr_filed_copy_dest_index, xt_userinfo.`xt_userinfo_realName` FROM solr_filed_copy solr_filed_copy LEFT JOIN solr_index solr_filed_copy_source ON solr_filed_copy_source.solr_index_id = solr_filed_copy.solr_filed_copy_source_id LEFT JOIN solr_index solr_filed_copy_dest ON solr_filed_copy_dest.solr_index_id = solr_filed_copy.solr_filed_copy_dest_id LEFT JOIN xt_userinfo AS xt_userinfo ON xt_userinfo.xt_userinfo_id = solr_filed_copy.xt_userinfo_id WHERE 1=1
AND solr_filed_copy.solr_core_id = #{solr_core_id}
AND solr_filed_copy.solr_filed_copy_source_id = #{solr_filed_copy_source_id}
AND solr_filed_copy.solr_filed_copy_dest_id = #{solr_filed_copy_dest_id}
AND solr_filed_copy.solr_filed_copy_remark like CONCAT('%','${solr_filed_copy_remark}','%' )
