mapper.select:
SELECT solr_index_attribute.`solr_index_attribute_id`, solr_index_attribute.`solr_index_attribute_facet`, solr_index_attribute.`solr_index_attribute_boost`, solr_index_attribute.`solr_index_attribute_spellcheck`, solr_index_attribute.`solr_index_attribute_status`, solr_index_attribute.`solr_index_attribute_issearch`, solr_index_attribute.`solr_index_id`, DATE_FORMAT(`solr_index_attribute_ctime`,'%Y-%m-%d %H:%i:%s') AS `solr_index_attribute_ctime`, DATE_FORMAT(`solr_index_attribute_mtime`,'%Y-%m-%d %H:%i:%s') AS `solr_index_attribute_mtime`, solr_index.`solr_index_name`, sor_index_highlight FROM solr_index_attribute AS solr_index_attribute LEFT JOIN solr_index AS solr_index ON solr_index.solr_index_id = solr_index_attribute.solr_index_id WHERE 1=1 AND solr_index_attribute.solr_index_attribute_status = 0
AND solr_index.solr_core_id = #{solr_core_id}
AND solr_index_attribute.solr_index_attribute_issearch = #{solr_index_attribute_issearch}
