mapper.select:
SELECT `xt_message_id`, `from_id`, `to_id`, `xt_meesage_content`, `isread`, DATE_FORMAT(`ctime`,'%Y-%m-%d %H:%i:%s') AS `ctime`, DATE_FORMAT(`readtime`,'%Y-%m-%d %H:%i:%s') AS `readtime`, xt_userinfo.xt_userinfo_realName as fromName, xtuserinfo.xt_userinfo_realName as toName FROM xt_message AS xt_message LEFT JOIN xt_userinfo AS xt_userinfo ON xt_userinfo.xt_userinfo_id = xt_message.from_id LEFT JOIN xt_userinfo AS xtuserinfo ON xtuserinfo.xt_userinfo_id = xt_message.to_id WHERE 1=1
AND isread = #{isread}
AND from_id = #{from_id}
AND to_id = #{to_id}
AND (xt_message.from_id=#{from_user_name_id} OR instr(xt_userinfo.xt_userinfo_realName,#{from_user_name_id}))
AND (xt_message.to_id=#{to_user_name_id} OR instr(xt_userinfo.xt_userinfo_realName,#{to_user_name_id}))
ORDER BY ctime ASC

SELECT
	from_id,
	COUNT(0) AS count
FROM 
	xt_message AS xt_message 
WHERE 1=1

	AND isread = #{isread}


	AND from_id = #{from_id}


	AND to_id = #{to_id}

GROUP BY from_id

mapper.update:
UPDATE
	xt_message
SET
	`isread` = #{isread},
	`readtime` = #{readtime}
WHERE 1=1
AND isread = 0
AND from_id = #{from_id}
AND to_id = #{to_id}
